/**
 * Import de la clase abstracta genérica **SearchableCollection**.
 */
import { SearchableCollection } from "./modification-searchablecollection";

/**
 * Subclase **NumericSearchableCollection** de la clase **SearchableCollection** que contiene el método *search*.
 * @method search -> Retorna un array con las posiciones en las que se encuentran coincidencias con el número *term* pasado por parámetro en la colección.
 */
export class NumericSearchableCollection extends SearchableCollection<number> {
  constructor(private _numberCollection: number[]) {
    super(_numberCollection);
  }

  /**
   * Método que retorna un array con las posiciones en las que se encuentran coincidencias con el número *term* pasado por parámetro en la colección.
   * @param term -> Número a buscar en la colección.
   * @returns Array con las posiciones de las coincidencias encontradas.
   */
  search(term: number): number[] {
    const numberCoincidences: number[] = [];
    for (let i = 0; i < this._numberCollection.length; i++) {
      if (this._numberCollection[i] === term) {
        numberCoincidences.push(i);
      }
    }
    return numberCoincidences;
  }
}

/**
 * Imports de las interfaces genéricas **Collectable** y **Searchable**.
 */
import { Collectable } from "./modification-collectable";
import { Searchable } from "./modification-searchable";

/**
 * Clase abstracta genérica **SearchableCollection** que implementa las interfaces genéricas **Collectable** y **Searchable** con sus respectivos métodos:
 * @private _items -> Array de los elementos genéricos.
 * @method addItem -> Añade un item.
 * @method getItem -> Retorna un item.
 * @method removeItem -> Elimina un item.
 * @method getNumberOfItems -> Retorna el número de items.
 * @method search -> Busca un item.
 */
export abstract class SearchableCollection<T> implements Collectable<T>, Searchable<T> {
  constructor(private _items: T[]) {
  }

  /**
   * Método que añade un item al atributo *_items*.
   * @param item -> Item a añadir.
   */
  addItem(item: T): T[] {
    this._items.push(item);
    return this._items;
  }

  /**
   * Método retorna un item del atributo *_items* según la posición en el mismo *indexOfItem*.
   * @param indexOfItem -> Índice del item en el array.
   * @returns -> Item obtenido según su índice.
   */
  getItem(indexOfItem: number): T | undefined {
    if (indexOfItem >= 0 && indexOfItem < this._items.length) {
      return this._items[indexOfItem];
    } else {
      return undefined;
    }
  }

  /**
   * Método que elimina del atributo *_items* un item según su posición *indexOfItem*.
   * @param indexOfItem -> Índice del item en el array.
   */
  removeItem(indexOfItem: number): T[] {
    return this._items.splice(indexOfItem, 1);
  }

  /**
   * Método que retorna el número de items en el atributo *_items*.
   * @returns -> Número de items en el array.
   */
  getNumberOfItems() {
    return this._items.length;
  }

  // Método de la interfaz genérica Searchable
  abstract search(term: T): T[]
}

/**
 * Import de la clase abstracta genérica **SearchableCollection**.
 */
import { SearchableCollection } from "./modification-searchablecollection";

/**
 * Subclase **StringSearchableCollection** de la clase **SearchableCollection** que contiene el método *search*.
 * @method search -> Retorna un array con las cadenas en las que se encuentran coincidencias con la cadena *term* pasada por parámetro en la colección.
 */
export class StringSearchableCollection extends SearchableCollection<string> {
  constructor(private _stringCollection: string[]) {
    super(_stringCollection);
  }

  /**
   * Método que retorna un array con las cadenas en las que se encuentran coincidencias con la cadena *term* pasada por parámetro en la colección.
   * @param term -> Cadena a buscar en la colección.
   * @returns Array con las coincidencias encontradas.
   */
  search(term: string): string[] {
    const stringCoincidences: string[] = [];
    for (let i = 0; i < this._stringCollection.length; i++) {
      if (this._stringCollection[i] === term) {
        stringCoincidences.push(term)
      }
    }
    return stringCoincidences;
  }
}
